    private void BtImcActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BtImcActionPerformed

        try{
            String nomer = CNome.getText();
            double peso = Double.parseDouble(CPeso.getText());
            double alt = Double.parseDouble(CAltura.getText());
            String Mensagem = "";
            Double resultado;
        
            resultado = peso / (alt * alt); 
        
        if(resultado<18.5){
            JOptionPane.showMessageDialog(null, "Abaixo do peso");
        }
        
        else if(resultado>=18.5 && resultado<24.9){
            JOptionPane.showMessageDialog(null, "Peso normal");
        }
        
        else if(resultado>=25 && resultado<29.9){
            JOptionPane.showMessageDialog(null, "Sobrepeso");
        }
        
        else if(resultado>= 30 && resultado<34.9){
            JOptionPane.showMessageDialog(null, "Obesidade grau 1");
        }
        
        else if(resultado>= 35 && resultado<39.9){
            JOptionPane.showMessageDialog(null, "Obesidade grau 2");
        }
        
        else{
            JOptionPane.showMessageDialog(null, "Obesidade grau 3");
        }
        
        Mensagem = "\nSeu nome: "+CNome.getText()+".";
        Painel.setText("Seu IMC: " + Double.toString(resultado)+Mensagem);
        }catch (NumberFormatException e){
        JOptionPane.showMessageDialog(null, "Informe valores v√°lidos", "Calculando IMC", JOptionPane.ERROR_MESSAGE);
    }//GEN-LAST:event_BtImcActionPerformed
    
}
